import React, { useState, useRef, useCallback } from 'react';
import usePlanetSearch from '../../hooks/usePlanetSearch';
import AutocompleteInput from './AutocompleteInput';
import SuggestionList from './SuggestionList';
import './Autocomplete.css';
import { debounce } from '../../utils/debounce';

const Autocomplete = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [selectedPlanet, setSelectedPlanet] = useState(null);
  const inputRef = useRef(null);

  // Create the debounced function using useCallback to ensure that it doesn't get recreated on every render
  const debouncedSetSearchTerm = useCallback(
    debounce((newSearchTerm) => {
      setSearchTerm(newSearchTerm);
    }, 500),
    []
  );

  // Hook that uses the search term state to fetch data
  const { planets, isLoading, isError } = usePlanetSearch(searchTerm);

  const handleInputChange = (event) => {
    debouncedSetSearchTerm(event.target.value);
    setSelectedPlanet(null);
  };

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  return (
    <div className={`autocomplete-container ${isFocused ? 'focused' : ''}`}>
      <AutocompleteInput
        searchTerm={searchTerm}
        handleInputChange={handleInputChange}
        handleFocus={handleFocus}
        handleBlur={handleBlur}
        inputRef={inputRef}
      />
      {isFocused && (
        <SuggestionList
          searchTerm={searchTerm}
          planets={planets}
          isLoading={isLoading}
          isError={isError}
          handleSelectPlanet={setSelectedPlanet}
        />
      )}
      {selectedPlanet && (
        <div className="selected-planet">
          <p>Selected Planet: {selectedPlanet.name}</p>
        </div>
      )}
    </div>
  );
};

export default Autocomplete;
