import useSWR from 'swr';
import { fetchPlanets } from '../api/starWarsApi';

const usePlanetSearch = (searchTerm) => {
    // Only call useSWR if searchTerm is not empty
    const { data: planets, error } = useSWR(searchTerm ? searchTerm : null, fetchPlanets, {
        revalidateOnFocus: false,
        revalidateOnReconnect: false,
        refreshInterval: 300000,
    });

    if (error) {
        console.error(`Error fetching planets: ${error.message}`);
    }

    return {
        planets: planets || [],
        isLoading: !error && !planets,
        isError: !!error, // Convert error to a boolean
    };
};

export default usePlanetSearch;
