import React, { useState, useRef, useEffect } from 'react';
import usePlanetSearch from '../../hooks/usePlanetSearch';
import AutocompleteInput from './AutocompleteInput';
import SuggestionList from './SuggestionList';
import './Autocomplete.css';

const Autocomplete = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [selectedPlanet, setSelectedPlanet] = useState(null);
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState(searchTerm);
  const inputRef = useRef(null);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 500);

    return () => {
      clearTimeout(handler);
    };
  }, [searchTerm]);

  const { planets, isLoading, isError } = usePlanetSearch(debouncedSearchTerm);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
    setSelectedPlanet(null);
  };

  const handleFocus = () => setIsFocused(true);
  const handleBlur = () => setIsFocused(false);

  if (selectedPlanet) {
    console.log('selectedPlanet: ', selectedPlanet);
  }
  return (
    <div className={`autocomplete-container ${isFocused ? 'focused' : ''}`}>
      <AutocompleteInput
        searchTerm={searchTerm}
        handleInputChange={handleInputChange}
        handleFocus={handleFocus}
        handleBlur={handleBlur}
        inputRef={inputRef}
      />
      {isFocused && (
        <SuggestionList
          isFocused={isFocused}
          searchTerm={searchTerm}
          planets={planets}
          isLoading={isLoading}
          isError={isError}
          handleSelectPlanet={setSelectedPlanet}
        />
      )}
      {selectedPlanet && (
        <div className="selected-planet">
          <p>Selected Planet: {selectedPlanet.name}</p>
        </div>
      )}
    </div>
  );
};

export default Autocomplete;
