import useSWR from 'swr';
import PropTypes from 'prop-types';
import { fetchPlanets } from '../api/starWarsApi';

const usePlanetSearch = (searchTerm) => {
    const { data: planets, error } = useSWR(searchTerm, fetchPlanets, {
        revalidateOnFocus: false, // Disable revalidation when the tab is in focus
        revalidateOnReconnect: false, // Disable revalidation when the network reconnects
        refreshInterval: 300000, // Refresh the data every 5 minutes (adjust as needed)
    });

    if (error) {
        console.error(`Error fetching planets: ${error.message}`);
    }

    return {
        planets: planets || [],
        isLoading: !error && !planets,
        isError: error,
    };
};

usePlanetSearch.propTypes = {
    searchTerm: PropTypes.string.isRequired,
};

export default usePlanetSearch;
